+        width: 1200,
+        height: container.scrollHeight + 40,
+        backgroundColor: '#ffffff',
+        scale: 2,
+        useCORS: true,
+        allowTaint: true,
+        scrollX: 0,
+        scrollY: 0
+    }).then(canvas => {
+        // Geçici container'ı kaldır
+        document.body.removeChild(container);
+        
+        // Canvas'ı blob'a dönüştür
+        canvas.toBlob(blob => {
+            if (navigator.share && navigator.canShare && navigator.canShare({files: [new File([blob], 'rapor.png', {type: 'image/png'})]})) {
