+                const file = new File([blob], `bayi-rapor-<?php echo date('Y-m-d'); ?>.png`, {type: 'image/png'});
+                navigator.share({
+                    title: 'Bayi Günlük Rapor',
+                    text: 'Bayi günlük satış raporu - <?php echo $displayDate; ?>',
+                    files: [file]
+                }).catch(err => console.log('Paylaşım iptal edildi'));
