+                // Masaüstü için download et ve WhatsApp web aç
+                const link = document.createElement('a');
+                link.download = `bayi-rapor-<?php echo date('Y-m-d'); ?>.png`;
+                link.href = canvas.toDataURL();
+                link.click();
+                
+                // WhatsApp Web'i aç (kullanıcı manuel olarak görseli yükleyebilir)
+                const message = encodeURIComponent('📈 Bayi Günlük Rapor - <?php echo $displayDate; ?>\n\n📊 Görsel rapor dosyası indirildi, lütfen yükleyiniz.');
+                window.open(`https://web.whatsapp.com/send?text=${message}`, '_blank');
+            }
+            
+            // Butonu normale döndür
+            button.disabled = false;
+            button.innerHTML = '<i class="fab fa-whatsapp me-1"></i>Paylaş';
+        }, 'image/png');
+    }).catch(error => {
+        console.error('Screenshot hatası:', error);
+        alert('Ekran görüntüsü alınamadı. Lütfen tekrar deneyin.');
+        
+        // Geçici container'ı temizle
+        if (document.body.contains(container)) {
+            document.body.removeChild(container);
+        }
+        
+        // Butonu normale döndür
+        button.disabled = false;
+        button.innerHTML = '<i class="fab fa-whatsapp me-1"></i>Paylaş';
+    });
+}
+</script>
+
