+        const originalClass = originalIcon.className;
+        
+        // İkonu geçici olarak değiştir
+        originalIcon.className = 'fas fa-check text-success';
+        
+        // 2 saniye sonra eski haline döndür
+        setTimeout(() => {
+            originalIcon.className = originalClass;
+        }, 2000);
+    }).catch(function(err) {
