+            // Submenu hover işlevselliği
+            const submenus = document.querySelectorAll('.dropdown-submenu');
+            
+            submenus.forEach(function(submenu) {
+                const toggle = submenu.querySelector('.dropdown-toggle');
+                const menu = submenu.querySelector('.dropdown-menu');
+                
+                // Hover ile açma (desktop)
+                if (window.innerWidth > 992) {
+                    submenu.addEventListener('mouseenter', function() {
+                        menu.style.display = 'block';
+                    });
+                    
+                    submenu.addEventListener('mouseleave', function() {
+                        menu.style.display = 'none';
+                    });
+                }
+                
+                // Click ile toggle (mobile)
+                toggle.addEventListener('click', function(e) {
+                    e.preventDefault();
+                    e.stopPropagation();
+                    
+                    if (window.innerWidth <= 992) {
+                        const isVisible = menu.style.display === 'block';
+                        menu.style.display = isVisible ? 'none' : 'block';
+                    }
+                });
+            });
+            
+            // Ana dropdown kapanırken submenüleri de kapat
+            const mainDropdown = document.querySelector('.navbar .dropdown');
+            if (mainDropdown) {
+                mainDropdown.addEventListener('hidden.bs.dropdown', function() {
+                    submenus.forEach(function(submenu) {
+                        const menu = submenu.querySelector('.dropdown-menu');
+                        menu.style.display = 'none';
+                    });
+                });
+            }
+        });
+    </script>

